import React ,{useState} from 'react';

import './ExpenseForm.css';

const ExpenseForm = () => {

  const [enteredTtitle,setEnteredTitle] = useState('',);

  const tittleChangeHandler = ( event ) =>{
    setEnteredTitle(event.target.value);
  };


  return (

    <form>
			<div className='new-expense__controls'>
				<div className='new-expense__control'>
					<label>Title</label>
					<input type='text' onChange={tittleChangeHandler}/>
				</div>
				<div className='new-expense__control'>
					<label>Amount</label>
					<input type='number' min='0.01' step='0.01' />
				</div>
				<div className='new-expense__control'>
					<label>Date</label>
					<input type='date' min='2020-01-01' max='2023-12-31' />
				</div>
			</div>
			<div className='new-expense__actions'>
				<button type='submit'>New Expense</button>
			</div>
		</form>
	
  );
};

export default ExpenseForm;
